import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';



const Authors = () => {
  const [authors, setAuthors] = useState([]);
  const [isLoading, setIsLoading] = useState(false);


  useEffect(() => {
    const getAuthors = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`${process.env.REACT_APP_BASE_URL}/users`)
        setAuthors(response.data)
      } catch (error) {
        console.log(error)
      }
      setIsLoading(false)
    }

    getAuthors();
  }, [])

  return (
    <section className="authors">
      <div className="container authors-container">
        {authors.length > 0 ? (
          authors.map(({ id, avatar, name, posts }) => (
            <div key={id} className="author-card">
              <Link to={`/posts/${id}`} className="author-link">
                <img src={`${process.env.REACT_APP_BASE_URL}/uploads/${avatar}`} alt={`Image of ${name}`} className="author-avatar" />
                <div className="author-info">
                  <h4 className="author-name">{name}</h4>
                  <p className="author-posts">{posts} Posts</p>
                </div>
              </Link>
            </div>
          ))
        ) : (
          <h2>No users/authors found.</h2>
        )}
      </div>
    </section>
  );
}

export default Authors;
