import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Avatar1 from '../components/images/author1.jpg';
import Avatar2 from '../components/images/author2.jpg';
import Avatar3 from '../components/images/author3.jpeg';
import Avatar4 from '../components/images/author4.jpg';



const Authors = () => {
  const [authors, setAuthors] = useState([]);
  const [isLoading, setIsLoading] = useState(false);


  useEffect

  return (
    <section className="authors">
      <div className="container authors-container">
        {authors.length > 0 ? (
          authors.map(({ id, avatar, name, posts }) => (
            <div key={id} className="author-card">
              <Link to={`/posts/${id}`} className="author-link">
                <img src={avatar} alt={`Image of ${name}`} className="author-avatar" />
                <div className="author-info">
                  <h4 className="author-name">{name}</h4>
                  <p className="author-posts">{posts} Posts</p>
                </div>
              </Link>
            </div>
          ))
        ) : (
          <h2>No users/authors found.</h2>
        )}
      </div>
    </section>
  );
}

export default Authors;
