import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Avatar from '../components/images/avatar2.jpg';
import { FaEdit, FaCheck } from 'react-icons/fa';

const UserProfile = () => {
  const [avatar, setAvatar] = useState('');
  return (
    <section className='profile'>
      <div className='container profile_container'>
        <Link to={`/myposts/sdfsdf`} className='btn'>
          My Stories
        </Link>

        <div className='profile_detail'>
          <div className='avatar_wrapper'>
            <div className='profile_avatar'>
              <img src={Avatar} alt='' />
              <button className='avatar_edit'>
                <label htmlFor='avatar'>
                  <FaEdit />
                </label>
                <input
                  type='file'
                  name='avatar'
                  id='avatar'
                  onChange={(e) => setAvatar(e.target.files[0])}
                  accept='png,jpg,jpeg'
                />
              </button>
            </div>
            <button className='profile_avatar-btn'>
              <FaCheck />
            </button>
          </div>

          <h1>Ernest Achiever</h1>
        </div>

        <div className='profile_form'>
          {/* Form details for updating user profile */}
        </div>
      </div>
    </section>
  );
};


export default UserProfile;
