import React, { useEffect, useState } from 'react';
import axios from 'axios'
import PostItem from './PostItem';
import Loader from './Loader';

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const fetchPosts = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`${process.env.REACT_APP_BASE_URL}/posts`);
        setPosts(response?.data); // Check if response.data is defined
      } catch (err) {
        console.log(err);
      }
      setIsLoading(false);
    }
  
    fetchPosts();
  }, []);
  

  if (isLoading) {
    return <Loader />;
  }

  return (
    <section className='posts'>
      {posts.length > 0 ? (
        <div className='container posts_container'>
          {posts.map(({ id, thumbnail, category, title, desc, authorID, createdAt }) => 
  <PostItem key={id} postID={id} thumbnail={thumbnail} category={category} title={title} desc={desc} authorID={authorID} createdAt={createdAt} />
)}

          )}
        </div>
      ) : (
        <h2 className='center'>No posts Found</h2>
      )}
    </section>
  );
}

export default Posts;
