import React, { useContext, useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import Loader from '../components/Loader';
import PostItem from '../components/PostItem';

const Posts = () => {
  const { category } = useParams();
  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const fetchPosts = async () => {
      setIsLoading(true);
      try {
        let url = `${process.env.REACT_APP_BASE_URL}/posts`;
        if (category) {
          url = `${process.env.REACT_APP_BASE_URL}/posts/categories/${category}`;
        }
        const response = await axios.get(url);
        setPosts(response.data);
      } catch (error) {
        console.log(error);
      }
      setIsLoading(false);
    }

    fetchPosts();
  }, [category]);

  return (
    <section className='posts'>
      <h2 className='posts__title'>{category ? `Posts in ${category}` : 'All Posts'}</h2>
      {isLoading ? (
        <Loader />
      ) : (
        <div className='posts__list'>
          {posts.length ? (
            posts.map(post => (
              <div key={post._id} className='posts__item'>
                <PostItem
                  postID={post._id}
                  category={post.category}
                  title={post.title}
                  desc={post.description}
                  authorID={post.creator}
                  thumbnail={post.thumbnail}
                  createdAt={post.createdAt}
                  rating={post.rating} // Pass the rating prop
                />
              </div>
            ))
          ) : (
            <p>No posts found</p>
          )}
        </div>
      )}
    </section>
  );
}

export default Posts;
