import React, { useState } from 'react';
import {Link} from 'rea'
import { FaEdit, FaCheck } from 'react-icons/fa';
import Avatar from '../components/images/avatar2.jpg';

const UserProfile = () => {
  const [avatar, setAvatar] = useState('');

  const handleAvatarChange = (e) => {
    setAvatar(e.target.files[0]);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Handle form submission to update user profile
  };

  return (
    <section className='profile'>
      <div className='container profile_container'>
        <Link to={`/myposts/sdfsdf`} className='btn'>My Stories</Link>

        <div className="profile_detail">
          <div className="avatar_wrapper">
            <div className="profile_avatar">
              <img src={avatar || Avatar} alt='User Avatar' />
              <label htmlFor='avatar' className='avatar_edit'><FaEdit /></label>
              <input type='file' name='avatar' id='avatar' onChange={handleAvatarChange} accept='image/png, image/jpeg' className='avatar_form_input' />
            </div>
          </div>
          <button className='profile_avatar-btn' onClick={handleFormSubmit}><FaCheck /></button>

          <div className="profile_details">
            <h1>Ernest Achiever</h1>
            <form className='form profile_form' onSubmit={handleFormSubmit}>
              <label htmlFor='username'>Username</label>
              <input type='text' id='username' name='username' defaultValue='Ernest Achiever' />

              <label htmlFor='email'>Email</label>
              <input type='email' id='email' name='email' defaultValue='ernest@example.com' />

              <label htmlFor='bio'>Bio</label>
              <textarea id='bio' name='bio' rows='4' defaultValue='Lorem ipsum dolor sit amet, consectetur adipiscing elit. Phasellus rhoncus, dui sit amet posuere interdum.'></textarea>

              <button type='submit' className='btn primary'>Save Changes</button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default UserProfile;
