import React, { useContext, useEffect, useState } from 'react';
import PostAuthor from '../components/PostAuthor';
import { Link, useParams } from 'react-router-dom';
import Loader from '../components/Loader';
import DeleteStory from './DeleteStory';
import { UserContext } from '../context/userContext';
import axios from 'axios';
import ReactHtmlParser from 'react-html-parser'; // Import the react-html-parser package

const PostDetail = () => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const { currentUser } = useContext(UserContext);

  useEffect(() => {
    const getPost = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`${process.env.REACT_APP_BASE_URL}/posts/${id}`);
        setPost(response.data);
      } catch (error) {
        const message = error.response && error.response.data.message ? error.response.data.message : error.message;
        setError(message);
      }
      setIsLoading(false);
    };

    getPost();
  }, [id]);

  if (isLoading) {
    return <Loader />;
  }

  return (
    <section className="post-detail">
      {error && <p className='error'>{error}</p>}
      {post && (
        <div className="container post-detail_container">
          <div className="post-detail_header">
            <PostAuthor authorID={post.creator} createdAt={post.createdAt} />
            {currentUser?.id === post?.creator && (
              <div className="post-detail_buttons">
                <Link to={`/posts/${post._id}/edit`} className='btn sm primary'>Edit</Link>
                <DeleteStory postId={id} />
              </div>
            )}
          </div>
          <h1>{post.title}</h1>
          <div className='post-detail_cover'>
            <img src={`${process.env.REACT_APP_ASSETS_URL}/uploads/${post.thumbnail}`} alt=" " />
          </div>
          <div>{ReactHtmlParser(post.description)}</div> {/* Render HTML content safely */}
        </div>
      )}
    </section>
  );
};

export default PostDetail;
