import React, { useState , useContext, useEffect} from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaEdit, FaCheck } from "react-icons/fa";
import { UserContext } from '../context/userContext';
import axios from 'axios';

const UserProfile = () => {
  const [avatar, setAvatar] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');

  const [isAvatarTouched, setIsAvatarTouched] = useState(false)

  const navigate = useNavigate();
  const { currentUser } = useContext(UserContext);
  const token = currentUser?.token;

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    const imageUrl = URL.createObjectURL(file);
    setAvatar(imageUrl);
  }

  useEffect(() => {
    if(!token){
      navigate('/login')
    }
  },[])

  const changeAvatarHandler= async () => {
    setIsAvatarTouched(false);
    try{
      const postData = new FormData();
      postData.set('avatar', avatar);
      const response = await axios.post(`${process.env.REACT_APP_BASE_URL}/users/change-avatar`, postData,
    {withCredentials: true, header})
    }catch(error){
      console.log(error)
    }

  }

  return (
    <section className='profile'>
      <div className='container profile_container'>
        <Link to={`/myposts/${currentUser.id}`} className='btn'>My Stories</Link>

        <div className="profile_detail">
          <div className="avatar_wrapper">
            <div className="profile_avatar">
              <img src={avatar} alt="Avatar" />
            </div>
            <form className='avatar_form'>
              <input type='file' name='avatar' id='avatar' onChange={handleAvatarChange} accept="image/png, image/jpeg" />
              <label htmlFor='avatar' onClick={() => setIsAvatarTouched(true)}><FaEdit /></label>
            </form>
            {isAvatarTouched && <button className='profile_avatar-btn' onClick={changeAvatarHandler}><FaCheck /></button>}
          </div>

          <h1>{currentUser.name}</h1>

          <form className='form profile_form'>
            <p className='form_error-message'>This is an error message</p>
            <input type='text' placeholder='Full Name' value={name} onChange={e => setName(e.target.value)} />
            <input type='email' placeholder='Email' value={email} onChange={e => setEmail(e.target.value)} />
            <input type='password' placeholder='Current Password' value={currentPassword} onChange={e => setCurrentPassword(e.target.value)} />
            <input type='password' placeholder='New Password' value={newPassword} onChange={e => setNewPassword(e.target.value)} />
            <input type='password' placeholder='Confirm New Password' value={confirmNewPassword} onChange={e => setConfirmNewPassword(e.target.value)} />
            <button type='submit' className='btn primary'>Update details</button>
          </form>
        </div>
      </div>
    </section>
  );
}

export default UserProfile;
