import React, { useContext, useState, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from 'axios'; // Import axios for making HTTP requests
import { UserContext } from '../context/userContext';
import Loader from '../components/Loader';
import DeleteStory from './DeleteStory';

const Dashboard = () => {
  const { currentUser } = useContext(UserContext);
  const navigate = useNavigate();
  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const { id } = useParams();
  const token = currentUser?.token;

  // Function to fetch posts
  const fetchPosts = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get(`${process.env.REACT_APP_BASE_URL}/posts/users/${id}`, {
        withCredentials: true,
        headers: { Authorization: `Bearer ${token}` }
      });
      setPosts(response.data);
    } catch (error) {
      console.log(error);
    }
    setIsLoading(false);
  };

  // Redirect to login page if user is not logged in
  useEffect(() => {
    if (!token) {
      navigate('/login');
    } else {
      fetchPosts();
    }
  }, [token, navigate, id]); // Add token, navigate, and id as dependencies to useEffect

  if (isLoading) {
    return <Loader />;
  }

  return (
    <section className='dashboard'>
      {posts.length ? (
        <div className='container dashboard_container'>
          {posts.map(post => (
            <article key={post.id} className='dashboard_post'>
              <div className="dashboard_post-info">
                <div className="dashboard_post-thumbnail">
                  <img src={`${process.env.REACT_APP_ASSETS_URL}/uploads/${post.thumbnail}`} alt={post.title} />
                </div>
                <h5>{post.title}</h5>
                <p>{post.summary}</p> {/* Display summary */}
              </div>
              <div className='dashboard_posts-actions'>
                <Link to={`/posts/${post._id}`} className='btn sm'>View</Link>
                <Link to={`/posts/${post._id}/edit`} className='btn sm primary'>Edit</Link>
                <DeleteStory postId={post._id} />
              </div>
            </article>
          ))}
        </div>
      ) : (
        <h2 className='center'>You have no posts yet</h2>
      )}
    </section>
  );
}

export default Dashboard;
