import React, { useState } from 'react';
import PostItem from './PostItem';

import { DUMMY_POSTS } from '../data';
const Posts = () => {
  const [posts] = useState(DUMMY_POSTS);
  return (
    <section className='posts'>
        {posts.length > 0 ? <div className='container posts_container'>
            {posts.map(({ id, storycover, category, title, desc, authorID }) => 
            <PostItem key={id} postID={id} storycover={storycover} category={category} title={title} desc={desc} authorID={authorID} />
        )}
        </div>: <h2 className=''>No posts Found</h2>}
    </section>
  );
}

export default Posts;
