import React, { useState, useContext } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css'
const CreateStory = () => {
  const [title, setTitle] = useState('');
  const [category, setCategory] = useState('Uncategorized');
  const [description, setDescription] = useState('');
  const [storycover, setStoryCover] = useState('');

  const {currentUser} = useContext(u)

  const modules = {
    toolbar: [
      [{'header':[1,2,3,4,5,6, false]}],
      ['bold', 'italic','underline','strike','blockquote'],
      [{'list':'ordered'},{'list':'bullet'},{'indent':'-1'},{'indent':'+1'}],
      ['link','image'],
      ['clean']
    ],
  };

  const formats = [
    'header',
    'bold','italic','strike','blockquote',
    'list','bullet','indent',
    'link','image'
  ];

  const POST_CATEGORIES = ["Adventure","Fiction","Horror","Mystery","Paranormal","Science Fiction","Thriller","Fantasy","Humor","Romance","Historical Fiction"];

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logic to handle form submission
  };

  return (
    <section className='create-post'>
      <div className='container'>
        <h2>Create a Story</h2>
        <p className='form_error-message'>
          This is an error message
        </p>
        <form className="form create_form" onSubmit={handleSubmit}>
          <input type='text' placeholder='Title' value={title} onChange={e => setTitle(e.target.value)} autoFocus/>
          <select name='category' value={category} onChange={e=> setCategory(e.target.value)}>
            {
            POST_CATEGORIES.map(cat => <option key={cat}>{cat}</option>)
            }
          </select>
          <ReactQuill modules={modules} formats={formats} value={description} onChange={setDescription} />
          <input type="file" onChange={e => setStoryCover(e.target.files[0])} accept='image/png,image/jpeg' />
          <button type='submit' className='btn primary'>Create</button>
        </form>
      </div>
    </section>
  );
}

export default CreateStory;
