import React, { useContext, useState } from 'react'
import {Link} from "react-router-dom"
import Logo from './images/Logo.png'
import {FaBars} from "react-icons/fa"
import {AiOutlineClose} from "react-icons/ai"

import { UserContext } from '../context/userContext'

const Header = () => {
  const [isNavShowing, setIsNavShowing] = useState(false);
  const {currentUser} = useContext(UserContext)

  const closeNavHandler = () => {
    if(window.innerWidth < 800){
      setIsNavShowing(false);
    } else{
      setIsNavShowing(true)
    }
  }
  useEffect(() => {
    const handleResize = () => {
      setIsNavShowing(window.innerWidth > 800);
    };
  
    // Set the initial state
    handleResize();
  
    // Add event listener
    window.addEventListener('resize', handleResize);
  
    // Remove event listener on cleanup
    return () => window.removeEventListener('resize', handleResize);
  }, []);
  return (

    <nav>
      <div className='container nav_container'>
        <Link to="/" className='nav__logo' onClick={closeNavHandler}>
          <img src={Logo} alt="Navbar Logo" />
        </Link>
        {currentUser?.id && isNavShowing && <ul className='nav_menu'>
          <li><Link to = {`/profile/${currentUser.id}`} onClick={closeNavHandler}>{currentUser?.name}</Link></li>
          <li><Link to = '/create'onClick={closeNavHandler}>CreateStory</Link></li>
          <li><Link to = '/authors'onClick={closeNavHandler}>Authors</Link></li>
          <li><Link to = '/logout'onClick={closeNavHandler}>Logout</Link></li>
        </ul>}
        {!currentUser?.id && isNavShowing && <ul className='nav_menu'>
         
          <li><Link to = '/authors'onClick={closeNavHandler}>Authors</Link></li>
          <li><Link to = '/login'onClick={closeNavHandler}>Login</Link></li>
        </ul>}
        <button className="nav__toggle-btn" onClick={() => setIsNavShowing(!isNavShowing)}>
          {isNavShowing ? <AiOutlineClose /> : <FaBars />}
        </button>
      </div>
    </nav>
  )
}

export default Header
