import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import { UserContext } from '../context/userContext';
import { Categories } from '../data';

const CreateStory = () => {
  const { currentUser } = useContext(UserContext);
  const history = useHistory();
  const [formData, setFormData] = useState({
    title: '',
    category: '',
    description: '',
    thumbnail: null,
  });
  const [error, setError] = useState('');

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = e => {
    setFormData({ ...formData, thumbnail: e.target.files[0] });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');

    const { title, category, description, thumbnail } = formData;

    if (!title || !category || !description || !thumbnail) {
      setError('Please fill in all fields and choose a thumbnail.');
      return;
    }

    const formDataToSend = new FormData();
    formDataToSend.append('title', title);
    formDataToSend.append('category', category);
    formDataToSend.append('description', description);
    formDataToSend.append('thumbnail', thumbnail);

    try {
      await axios.post(`${process.env.REACT_APP_BASE_URL}/posts`, formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${currentUser.token}`,
        },
      });
      history.push('/dashboard');
    } catch (err) {
      console.error(err);
      setError('Something went wrong. Please try again later.');
    }
  };

  return (
    <div className='create-story'>
      <h2>Create a New Story</h2>
      {error && <div className='error'>{error}</div>}
      <form onSubmit={handleSubmit}>
        <div className='form-group'>
          <label htmlFor='title'>Title:</label>
          <input type='text' id='title' name='title' onChange={handleChange} />
        </div>
        <div className='form-group'>
          <label htmlFor='category'>Category:</label>
          <select id='category' name='category' onChange={handleChange}>
            <option value=''>Select a category</option>
            {Categories.map((category, index) => (
              <option key={index} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>
        <div className='form-group'>
          <label htmlFor='description'>Description:</label>
          <textarea id='description' name='description' onChange={handleChange}></textarea>
        </div>
        <div className='form-group'>
          <label htmlFor='thumbnail'>Thumbnail:</label>
          <input type='file' id='thumbnail' name='thumbnail' accept='image/*' onChange={handleFileChange} />
        </div>
        <button type='submit'>Create Story</button>
      </form>
    </div>
  );
};

export default CreateStory;
