import React, { useState, useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { DUMMY_POSTS } from '../data';
import { UserContext } from '../context/userContext';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const { currentUser } = useContext(UserContext);
  const navigate = useNavigate(); // Moved inside component body
  const token = currentUser?.token;
  
  // Redirect to login page if user is not logged in
  useEffect(() => {
    if (!token) {
      navigate('/login');
    }
  }, [token, navigate]); // Added dependencies to useEffect

  return (
    <section className='dashboard'>
      {DUMMY_POSTS.length ? (
        <div className='container dashboard_container'>
          {DUMMY_POSTS.map(post => (
            <article key={post.id} className='dashboard_post'>
              <div className="dashboard_post-info">
                <div className="dashboard_post-thumbnail">
                  <img src={post.storycover} alt={post.title} /> {/* Added alt attribute */}
                </div>
                <h5>{post.title}</h5>
              </div>
              <div className='dashboard_posts-actions'>
                <Link to={`/posts/${post.id}`} className='btn sm'>View</Link>
                <Link to={`/posts/${post.id}/edit`} className='btn sm primary'>Edit</Link>
                <Link to={`/posts/${post.id}/delete`} className='btn sm danger'>Delete</Link>
              </div>
            </article>
          ))}
        </div>
      ) : (
        <h2 className='center'>You have no posts yet</h2>
      )}
    </section>
  );
}

export default Dashboard;
