import React, { useContext, useEffect, useState } from 'react';
import PostAuthor from '../components/PostAuthor';
import { Link, useParams } from 'react-router-dom';
import Loader from '../components/Loader';
import DeleteStory from './DeleteStory';
import { UserContext } from '../context/userContext';
import axios from 'axios';
import ReactHtmlParser from 'react-html-parser';


const PostDetail = () => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const { currentUser } = useContext(UserContext);
  const [summary, setSummary] = useState('');
  const [ratings, setRatings] = useState([]);
  const [comments, setComments] = useState([]);

  useEffect(() => {
    const getPost = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`${process.env.REACT_APP_BASE_URL}/posts/${id}`);
        setPost(response.data);
      } catch (error) {
        const message = error.response && error.response.data.message ? error.response.data.message : error.message;
        setError(message);
      }
      setIsLoading(false);
    };

    const fetchSummary = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_BASE_URL}/posts/${id}/summary`);
        setSummary(response.data.summary);
      } catch (error) {
        console.error('Error fetching summary:', error);
      }
    };

    const fetchRatings = async () => {
      // Static ratings for demonstration
      const staticRatings = [
        { user: 'User1', rating: 4 },
        { user: 'User2', rating: 5 },
        { user: 'User3', rating: 3 },
        { user: 'User4', rating: 2 },
      ];
      setRatings(staticRatings);
    };

    const fetchComments = async () => {
      // Static comments for demonstration
      const staticComments = [
        { user: 'User1', comment: 'Great story!' },
        { user: 'User2', comment: 'Interesting plot.' },
        { user: 'User3', comment: 'Could be better.' },
      ];
      setComments(staticComments);
    };

    getPost();
    fetchSummary();
    fetchRatings();
    fetchComments();
  }, [id]);

  if (isLoading) {
    return <Loader />;
  }

  return (
    <section className="post-detail">
      {error && <p className='error'>{error}</p>}
      {post && (
        <div className="container post-detail_container">
          <div className="post-detail_header">
            <PostAuthor authorID={post.creator} createdAt={post.createdAt} />
            {currentUser?.id === post?.creator && (
              <div className="post-detail_buttons">
                <Link to={`/posts/${post._id}/edit`} className='btn sm primary'>Edit</Link>
                <DeleteStory postId={id} />
              </div>
            )}
          </div>
          <h1>{post.title}</h1>
          <div className='post-detail_cover'>
            <img src={post.thumbnail} alt=" " />
          </div>
          <div>{ReactHtmlParser(post.description)}</div>
          {summary && (
            <div className="post-detail_summary">
              <h3>Summary:</h3>
              <p>{summary}</p>
            </div>
          )}
          <div className="post-detail_ratings">
            <h3>Ratings:</h3>
            {ratings.map((rating, index) => (
              <div key={index} className="rating-card">
                <p>User: {rating.user}</p>
                <p>Rating: {rating.rating}</p>
              </div>
            ))}
          </div>
          <div className="post-detail_comments">
            <h3>Comments:</h3>
            {comments.map((comment, index) => (
              <div key={index} className="comment-card">
                <p>User: {comment.user}</p>
                <p>Comment: {comment.comment}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </section>
  );
};

export default PostDetail;
