import React, { useContext, useState, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from 'axios'; // Import axios for making HTTP requests
import { UserContext } from '../context/userContext';
import Loader from '../components/Loader'


const Dashboard = () => {
  const { currentUser } = useContext(UserContext);
  const navigate = useNavigate();
  const [posts, setPosts] = useState([]);
  

  const token = currentUser?.token;

  // Fetch posts from the server
  const fetchPosts = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_BASE_URL}/posts?timestamp=${Date.now()}`, {
        headers: {
          Authorization: `Bearer ${token}` // Send the token in the request headers
        }
      });
      setPosts(response.data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  // Redirect to login page if user is not logged in
  useEffect(() => {
    if (!token) {
      navigate('/login');
    } else {
      fetchPosts(); // Fetch posts when the component mounts or token changes
    }
  }, [token, navigate]); // Add token and navigate as dependencies to useEffect

  return (
    <section className='dashboard'>
      {posts.length ? (
        <div className='container dashboard_container'>
          {posts.map(post => (
            <article key={post.id} className='dashboard_post'>
              <div className="dashboard_post-info">
                <div className="dashboard_post-thumbnail">
                  <img src={`${process.env.REACT_APP_ASSETS_URL}/${post.thumbnail}`} alt={post.title} />
                </div>
                <h5>{post.title}</h5>
              </div>
              <div className='dashboard_posts-actions'>
                <Link to={`/posts/${post.id}`} className='btn sm'>View</Link>
                <Link to={`/posts/${post.id}/edit`} className='btn sm primary'>Edit</Link>
                <Link to={`/posts/${post.id}/delete`} className='btn sm danger'>Delete</Link>
              </div>
            </article>
          ))}
        </div>
      ) : (
        <h2 className='center'>You have no posts yet</h2>
      )}
    </section>
  );
}

export default Dashboard;
