import React, { useContext, useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Loader from './';
import { UserContext } from '../context/userContext';

const Dashboard = () => {
  const { currentUser } = useContext(UserContext);
  const navigate = useNavigate(); // Moved inside component body
  const token = currentUser?.token;
  const [userPosts, setUserPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  // Fetch user's posts
  useEffect(() => {
    const fetchUserPosts = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`${process.env.REACT_APP_BASE_URL}/posts/users/${currentUser.id}`);
        if (response && response.data) {
          setUserPosts(response.data);
        } else {
          setUserPosts([]); // Set empty array if response or response.data is undefined
        }
      } catch (err) {
        console.log(err);
      }
      setIsLoading(false);
    };
  
    if (token) {
      fetchUserPosts();
    } else {
      navigate('/login');
    }
  }, [token, currentUser.id, navigate]);

  if (isLoading) {
    return <Loader />;
  }

  return (
    <section className='dashboard'>
      {userPosts.length ? (
        <div className='container dashboard_container'>
          {userPosts.map(post => (
            <article key={post._id} className='dashboard_post'>
              <div className="dashboard_post-info">
                <div className="dashboard_post-thumbnail">
                  <img src={`${process.env.REACT_APP_BASE_URL}/uploads/${post.thumbnail}`} alt={post.title} />
                </div>
                <h5>{post.title}</h5>
              </div>
              <div className='dashboard_posts-actions'>
                <Link to={`/posts/${post._id}`} className='btn sm'>View</Link>
                <Link to={`/posts/${post._id}/edit`} className='btn sm primary'>Edit</Link>
                <Link to={`/posts/${post._id}/delete`} className='btn sm danger'>Delete</Link>
              </div>
            </article>
          ))}
        </div>
      ) : (
        <h2 className='center'>You have no posts yet</h2>
      )}
    </section>
  );
}

export default Dashboard;
