import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid'; // Import UUID generator
import { DUMMY_POSTS } from '../data';
import PostItem from '../components/PostItem';

const AuthorStory = () => {
  const [posts, setPosts] = useState(DUMMY_POSTS.map(post => ({ ...post, id: uuidv4() }))); // Ensure each post has a unique ID
  return (
    <section className='author_posts'>
      {posts.length > 0 ? (
        <div className='container author_posts-conatiner'>
          {posts.map(({ id, storycover, category, title, desc, authorID }) => 
            <PostItem key={id} postID={id} storycover={storycover} category={category} title={title} desc={desc} authorID={authorID} />
          )}
        </div>
      ) : (
        <h2 className='center'>No posts Found</h2>
      )}
    </section>
  );
};

export default AuthorStory;
